class User {
  final String name;
  final String id;
  String img;
  bool isFirstYear;
  User({this.name, this.id, this.img}) {
    if (double.parse(id.substring(0, 1)) == DateTime.now().year) {
      this.isFirstYear = true;
    } else {
      this.isFirstYear = false;
    }
    if (!img.contains("base64"))
      this.img =
          "data:image/jpeg;base64,";
  }
}
